import "./App.css";
import { makeStyles } from "@material-ui/core/styles";
import { Typography, TextField } from "@material-ui/core";

import { withStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import React, { Component } from "react";
import Button from "@material-ui/core/Button";
//import About from './About.js';

const styles = makeStyles(theme => ({
  button: {
    margin: theme.spacing(2),
    marginTop: 10,
    marginBottom: 10
  },
  textfield: {
    margin: theme.spacing(2)
  },
  paper: {
    verticalAlign: "middle",
    width: 400
  },
  container: {
    display: "flex",
    margin: theme.spacing(5),
    marginLeft: 300
  }
}));

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      project_name: "",
      description: "",
      start_date: "",
      end_date: "",
      goal: 0,
      about: false,
    };
  }

  setExample() {
    this.setState({
      about: false,
      project_name: "Worlds & Creatures, the Art of Anthony Christou",
      start_date: "2017-05-08",
      end_date: "2017-05-29",
      goal: "5000",
      description:
        "A 100 page Artbook featuring the work of Anthony Christou over the last 5 years. Art from the genres of fantasy, myths, horror & more."
    });
  }
  setHome() {
    this.setState({

      project_name: "",
      description: "",
      start_date: "",
      end_date: "",
      goal: 0,
      about: false,
     });
  }

  setAbout() {
    this.setState({
      about: true,
     });
  }

  descriptionUpdate(event) {
    this.setState({description: event.target.value});
  }
  projectNameUpdate(event) {
    this.setState({project_name: event.target.value});
  }
  startDateUpdate(event) {
    this.setState({start_date: event.target.value});
  }
  endDateUpdate(event) {
    this.setState({end_date: event.target.value});
  }
  goalUpdate(event) {
    this.setState({goal: event.target.value});
  }

  render() {
    console.log(this.state);
    const classes = this.props.classes;

    return (
      <div className="App">
        <header className="App-header">
          <h1> Welcome to Kickstarter Prediction</h1>
          <div>
            <Button color="secondary" className={classes.button}
            onClick={() => this.setHome()}>
              Home
            </Button>
            <Button
              color="secondary"
              className={classes.button}
              onClick={() => this.setExample()}
            >
              Project example
            </Button>
            <Button color="secondary" className={classes.button}
            onClick={() => this.setAbout()}>
              About
            </Button>
          </div>
        </header>

          {this.state.about ? 
          "About blah blah blah"
          :
          <h2>
          <div className={classes.container}>
            <Paper className={classes.paper}>
              <Typography variant="h5">
                Please enter your project details for prediction
              </Typography>
              <TextField
                id="outlined-basic"
                className={classes.textfield}
                label="Project name"
                margin="normal"
                variant="outlined"    
                onChange={this.projectNameUpdate.bind(this)}
                value={this.state.project_name}
              >
              </TextField>
              <TextField
                id="date"
                label="Project startdate"
                type="date"
                variant="outlined"
                className={classes.textfield}
                InputLabelProps={{
                  shrink: true
                }}
                onChange={this.startDateUpdate.bind(this)}
                value={this.state.start_date}
              />
              <TextField
                id="date"
                label="End date"
                type="date"
                variant="outlined"
                className={classes.textfield}
                InputLabelProps={{
                  shrink: true
                }}
                onChange={this.endDateUpdate.bind(this)}
                value={this.state.end_date}
              />
              <TextField
                id="outlined-basic"
                className={classes.textfield}
                label="Goal"
                margin="normal"
                variant="outlined"              
                onChange={this.goalUpdate.bind(this)}
                value={this.state.goal}
              />
              <TextField
                className={classes.textfield}
                id="outlined-multiline-static"
                label="Blurb"
                multiline
                rows="4"
                //defaultValue="Please enter your project description"
                margin="normal"
                variant="outlined"
                onChange={this.descriptionUpdate.bind(this)}
                value={this.state.description}
              />
              <Button
                variant="contained"
                color="primary"
                className={classes.button}
              >
                Start to predict
              </Button>
            </Paper>
            <Paper style={{ marginLeft: 50, width: 400 }}>
              <h2>Prediction Result</h2>
            </Paper>
          </div>
        </h2>
  }
      </div>
    );
  }
}

export default withStyles(styles)(App);
